<?phprequire( __DIR__ . '/omoclass_common.php' );//. def project_m( '全ID取得開始' );$o_db_in = new cls_sqlite( DN_DATA . '/profdb_s.sqlite' );$ids_all = $o_db_in->qcol([ 'select' => 'id' ]);_m( '全ID取得完了' );$ids = [];foreach ( array_rand( $ids_all, NUM_DATASET ) as $i ) {	$ids[] = $ids_all[$i];}unset( $ids_all );_m( 'ランダム選択完了' );//_m( _imp( $ids ) );//. DB作成$o_db_out = _prep_profdb( FN_DB );//. 書き込みforeach ( $ids as $id  ) {	_count( 100 );	$bin = _get_data( $o_db_in, $id );	$pca = _bin2prof( $bin )[3];	$o_db_out->set([ $id, $bin, $pca[0], $pca[1], $pca[2] ]);}$o_db_out->end();