<?phprequire( __DIR__ . '/omoclass_common.php' );//. ID決定$o_db = new cls_sqlite( FN_DB );$ids_all = $o_db->qcol([ 'select' => 'id' ]);$ids = [];foreach ( array_rand( $ids_all, NUM_CLASS ) as $i ) {	$ids[] = $ids_all[$i];}unset( $ids_all );//. プロファイル書き込み$o_nodedb = _prep_profdb( 0 );foreach ( $ids as $num => $id ) {	unset( $data, $pca1, $pca2, $pca3 );	extract(		$o_db->where([ "id=\"$id\"" ])			->qar([ 'select' => [ 'data', 'pca1', 'pca2', 'pca3' ] ])[0]	);	$o_nodedb->set([ $num, $data, $pca1, $pca2, $pca3 ]);}$o_nodedb->end();_json_save( DN_PROJ . '/init_ids.json', $ids );