<?phprequire( __DIR__ . '/omoclass_common.php' );//. ID決定$o_db = new cls_sqlite( FN_DB );$ids_all = $o_db->qcol([ 'select' => 'id', 'where' => 'pca1 * pca2 * pca3 > 7000' ]);//_pause( count( $ids_all ) );$ids = [];foreach ( array_rand( $ids_all, NUM_CLASS ) as $i ) {	$ids[] = $ids_all[$i];}unset( $ids_all );//. プロファイル書き込み$o_db = new cls_sqlite( DN_DATA . '/profdb_s.sqlite' );$o_nodedb = _prep_profdb( 0 );foreach ( $ids as $num => $id ) {	unset( $data, $pca1, $pca2, $pca3 );	$bin = _get_data( $o_db, $id );	$pca = _bin2prof( $bin )[3];//	_pause( json_encode( $pca ) );	$o_nodedb->set([ $num, $bin, $pca[0], $pca[1], $pca[2] ]);}$o_nodedb->end();_json_save( DN_PROJ . '/init_ids.json', $ids );