<?phprequire( __DIR__ . '/omoclass_common.php' );define( 'FN_DOING', DN_PROJ . '/_doing.txt' );if ( file_exists( FN_DOING ) )	_die( PROJ_NAME . ': 実行中' ); touch( FN_DOING );define( 'SCRIPT_INIT', PROJ_INFO[ 'script_init' ] ?: 'omoclass_prof0' );define( 'SCRIPT_CLS',  PROJ_INFO[ 'script_cls'  ] ?: 'omoclass_classify_1by1' );define( 'SCRIPT_AVG',  PROJ_INFO[ 'script_avg'  ] ?: 'omoclass_average_1by1' );if ( ! file_exists( _fn2( 'profdb', 0 ) ) ) {	_php( SCRIPT_INIT, PROJ_NAME );}while ( true ) {	if ( file_exists( DN_TEMP . '/proc/stop' ) ) break;	_php( SCRIPT_CLS, PROJ_NAME );	_php( SCRIPT_AVG, PROJ_NAME );	$num = _fn2num( _fn_last( 'profdb' ) );	if ( $num == 1000 ) break;	$step = _get_step_val( $num );	if ( $step[ 'min' ] == 1 ) {		_m( 'Converged' );		break;	}}_del( FN_DOING );_php( 'omoclass_info', PROJ_NAME );